    def fontsizeshow(self):
        if self.comboBox.currentText()== "Top" or self.comboBox.currentText()=='Right':
            self.spinBox_2.setVisible(False)
            self.label_28.setVisible(False)
        if self.comboBox.currentText() == 'Left' or self.comboBox.currentText() =='Bottom':
            self.spinBox_2.setVisible(True)
            self.label_28.setVisible(True)

    def labelshow(self):
        if self.checkBox.isChecked():
            self.lineEdit.setVisible(True)
            if self.checkBox_2.isChecked():
                self.lineEdit_2.setVisible(True)
                self.lineEdit.setText(
                    self.all_information.layers.xlabel + ' ' + '(' + self.all_information.layers.x_ticket.units + ')')
                self.lineEdit_2.setText(
                    self.all_information.layers.ylabel + ' ' + '(' + self.all_information.layers.y_ticket.units + ')')

            else:
                self.lineEdit_2.setVisible(False)
                self.lineEdit.setText(
                    self.all_information.layers.xlabel + ' ' + '(' + self.all_information.layers.x_ticket.units + ')')

        else:
            self.lineEdit.setVisible(False)
            if self.checkBox_2.isChecked():
                self.lineEdit_2.setVisible(True)
                self.lineEdit_2.setText(
                    self.all_information.layers.ylabel + ' ' + '(' + self.all_information.layers.y_ticket.units + ')')

            else:
                self.lineEdit_2.setVisible(False)


